import React from 'react';

const IntroSection = () => {
  const styles = {
    container: {
      display: 'flex',
      padding: '60px',
      backgroundColor: 'white',
      gap: '20px',
    },
    leftBox: {
      flex: 1,
    //   backgroundColor: '#e8f5e9',
      padding: '30px',
      position: 'relative',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between',
      borderRadius: '10px',
    },
    heading: {
      fontSize: '2rem',
      fontWeight: 'bold',
      color: 'black',
      marginBottom: '15px',
    },
     heading2: {
      fontSize: '1.5rem',
      fontFamily: "'Covered By Your Grace', cursive",
      fontWeight: 'bold',
      color: '#2e7d32',
      marginBottom: '15px',
    },
    description: {
      fontSize: '19px',
      color: '#444',
      lineHeight: '1.5',
    },
    leftBottomBox: {
      flex: 'none',
      padding: '30px',
      position: 'absolute',
      display: 'flex',
      flexDirection: 'column',
      marginTop:' 312px',
      justifyContent: 'space-between',
      borderRadius: '10px',
    },
    cornerImage: {
      position: 'absolute',
      bottom: '7px',
      right: '15px',
      width: '60px',
      height: '60px',
      objectFit: 'cover',
      borderRadius: '8px',
    },
    middleBox: {
      flex: 1,
      display: 'flex',
      flexDirection: 'column',
      gap: '15px',
    },
    middleTopImg: {
      flex: 6,
      borderRadius: '10px',
      objectFit: 'cover',
      width: '247.95px',
     height: '411.92px',
     marginLeft:'200px',
    },
    middleBottomImg: {
      flex: 4,
      borderRadius: '10px',
      objectFit: 'cover',
      width: '240.95px',
      marginLeft: '200px',
      height: '87px',
    },
    rightBox: {
      flex: 1,
    },
    fullImage: {
      width: '296px',
      height: '538.72px',
      objectFit: 'cover',
      borderRadius: '10px',
    }
  };

  return (
    <section style={styles.container}>
      {/* Left Content Box */}
      <div style={styles.leftBox}>
        <div>
            <h3 style={styles.heading2}>Get to Know us</h3>
          <h2 style={styles.heading}>A Leader in Agricultural Market</h2>
          <p style={styles.description}>
            We offer modern farming tools, consultancy, and supplies to help farmers achieve sustainable success.
            Join us and be a part of the agri revolution.
          </p>
        </div>
        {/* <img
          src="data:image/webp;base64,"
          alt="Leaf Icon"
          style={styles.cornerImage}
        /> */}
      </div>

      {/* Middle Box with Two Images */}
      <div style={styles.middleBox}>
        <img
          src="https://static.wixstatic.com/media/80f7bf_e3bd2b6fcd574fe7b21b77bc19ab4e50~mv2.jpg/v1/crop/x_0,y_48,w_500,h_390/fill/w_367,h_286,al_c,q_80,usm_0.66_1.00_0.01,enc_auto/apple-farmer-v2.jpg"
          alt="Farmer Field"
          style={styles.middleTopImg}
        />
        <img
          src="data:image/webp;base64,"
          alt="Harvest"
          style={styles.middleBottomImg}
        />
      </div>

      {/* Right Full Image Box */}
      <div style={styles.rightBox}>
        <img
          src="https://c8.alamy.com/comp/2BMAMBG/tractor-on-the-grass-field-2BMAMBG.jpg"
          alt="Agri Full"
          style={styles.fullImage}
        />
      </div>
      {/* Left Bottom Content Box */}
      <div style={styles.leftBottomBox}>
        <div>
          <h3 style={styles.heading2}>Modern Agriculture Types</h3>
          <p style={styles.description}>
            We are here for you from start to finish
          </p>
          
        </div>
        </div>
    </section>
  );
};

export default IntroSection;